CREATE SCHEMA IF NOT EXISTS ecole;

DROP TABLE IF EXISTS eleves CASCADE;
DROP TABLE IF EXISTS classes CASCADE;


CREATE TABLE ecole.eleves (
	id INT,
	prenom VARCHAR,
	nom VARCHAR,
	classe_id INT,
	maths DOUBLE PRECISION,
	francais DOUBLE PRECISION,
	histoire DOUBLE PRECISION,
	moyenne DOUBLE PRECISION
);

CREATE TABLE ecole.classes (
	id INT,
	nom VARCHAR
);




